import { Form, Button } from "react-bootstrap";
import { useState } from "react";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AddEmployee.css';

function AddEmployee() {

    const navigate = useNavigate();
    const [employee, setEmployee] = useState({
        name:"",
        phone:"",
        address:"",
        position:"",
        salary:"",
    });

    const handleChange = (event) => {
        const { name, value} = event.target;

        setEmployee((prev) => {
            return {
                ...prev,
                [name]: value,
            };
        });
    };

    const handleClick = (event) => {
        if (validateForm()) {
            axios.post("/api/emp/create", employee)
            .then((res) => console.log(res))
            .catch((err) => console.log(err));

            navigate("employees");
        }
        
    }

    const [nameError, setNameError] = useState("");
    const [phoneError, setPhoneError] = useState("");
    const [addressError, setAddressError] = useState("");
    const [positionErrror, setPositionError] = useState("");
    const [salaryError, setSalaryError] = useState("");
    
    const validateForm = () => {
        let valid = true;

        const empName = document.getElementById('name');
        const empPhone = document.getElementById('phone');
        const empAddress = document.getElementById('address');
        const empPosition = document.getElementById('position');
        const empSalary = document.getElementById('salary');

        if (employee.name === '') {
            empName.setCustomValidity('Please Enter the name');
            setNameError("Please Enter the name");
            valid = false;
        } else {
            empName.setCustomValidity('');
            setNameError('');
        }

        if (employee.phone === '') {
            empPhone.setCustomValidity("Please Enter the Phone Number");
            setPhoneError("Please Enter the Phone Number");
            valid = false;
        } else if (employee.phone.length() !== 10) {
            empPhone.setCustomValidity("Please Enter a valid Phone Number");
            setPhoneError("Please Enter a valid Phone Number");
            valid = false;
        }else {
            empPhone.setCustomValidity('');
            setPhoneError('');
        }

        if (employee.address === '') {
            empAddress.setCustomValidity('Please Enter the Address');
            setAddressError("Please Enter the Address");
            valid = false;
        } else {
            empAddress.setCustomValidity('');
            setAddressError('');
        }

        if (employee.position === '') {
            empPosition.setCustomValidity('Please Select the position');
            setPositionError("Please Select the position");
            valid = false;
        } else {
            empPosition.setCustomValidity('');
            setPositionError('');
        }

        if (employee.salary === '') {
            empSalary.setCustomValidity("Please Enter the Salary");
            setSalaryError("Please Enter the Salary");
            valid = false;
        } else if (isNaN(employee.salary)) {
            empSalary.setCustomValidity("Please Enter a valid Salary");
            setSalaryError("Please Enter a valid Salary");
            valid = false;
        }else {
            empSalary.setCustomValidity('');
            setSalaryError('');
        }

        return valid;
    }

    return(
        <div className="add">
            <Form className="form">
                <h1>Add Employee</h1>
                <Form.Group className="form-group">
                    <Form.Control className="form-control"
                        name="name" 
                        id="name"
                        placeholder="Enter Employee Name"
                        onChange={handleChange}
                        value={employee.name}
                    />
                    { nameError && <div className="error">{nameError}</div> }
                    <br /><br />

                    <Form.Control className="form-control"
                        name="phone" 
                        id="phone"
                        placeholder="Enter Employee Contact-No"
                        onChange={handleChange}
                        value={employee.phone}
                    />
                    { phoneError && <div className="error">{phoneError}</div> }
                    <br /><br />

                    <Form.Control className="form-control"
                        name="address" 
                        id="addresss"
                        placeholder="Enter Employee Address"
                        onChange={handleChange}
                        value={employee.address}
                    />
                    { addressError && <div className="error">{addressError}</div> }
                    <br /><br />

                    <Form.Select className="form-control" 
                        name="position" 
                        id="position"
                        onChange={handleChange}
                        value={employee.position}
                        style={{color: "white", paddingLeft: "0.5%", width: "52%"}}
                    >
                        { positionErrror && <div className="error">{positionErrror}</div> }
                            <option>Select Job Role</option>
                            <option>Manager</option>
                            <option>Employee</option>
                            <option>Intern</option>
                    </Form.Select>
                    <br /><br />

                    <Form.Control className="form-control"
                        name="salary" 
                        id="salary"
                        placeholder="Enter Employee Salary"
                        onChange={handleChange}
                        value={employee.salary}
                    />
                    { salaryError && <div className="error">{salaryError}</div> }
                    <br /><br />
                </Form.Group>
                <Button onClick={handleClick}>Add Employee</Button>
            </Form>
        </div>
    )
}

export default AddEmployee;
