import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import { useEffect, useState } from 'react';
import './Employee.css';

function Employee(){

    const [emp, setEmp] = useState([]);

    useEffect(() => {
        axios.get("/api/emp/posts")
            .then((res) => {
                console.log(res)
                setEmp(res.data);
            })
            .catch((err) => console.log(err));
    }, []);

    return (
        <div>
            <h1>Employee Details</h1>
            {emp ? (

                <div className="container">

                <table className='table'>
                    <thead>
                        <tr>
                            <th scope='col' className='head'>Employee Name</th>
                            <th scope='col' className='head'>Employee Address</th>
                            <th scope='col' className='head'>Employee Contact</th>
                            <th scope='col' className='head'>Employee Position</th>
                            <th scope='col' className='head'>Employee Salary</th>
                        </tr>
                    </thead>
                    

                <>
                    {emp.map(post => {
                        return(
                            <tbody>
                                <tr>
                                    <td>{post.name}</td>
                                    <td>{post.address}</td>
                                    <td>{post.phone}</td>
                                    <td>{post.position}</td>
                                    <td>{post.salary}</td>
                                    <td>
                                        <button style={{width: "80%",marginLeft:'10px', backgroundColor:"#B7D6B6", border:"#B7D6B6"}}>Update</button>
                                    </td>
                                    <td style={{marginRight:"5%"}}>
                                        <button style={{width: "80%",marginLeft:'10px'}}>Delete</button>
                                    </td>
                                </tr>
                            </tbody>
                        )
                    })}
                </>
                </table>
                </div>

            ) :""}
        </div>
    );
}

export default Employee;
